import React, { useState, useEffect } from "react";
import DragAndDropImageInput from "./components/DragNDrop";
import Header from "./components/Header";
import { getCurrentUser } from "./app.js"; // Adjust this import as per your actual implementation

const config = require("./configs/config.js");

const AddItem = () => {
  const [images, setImages] = useState(Array(8).fill({ file: null, name: "" }));
  const [formData, setFormData] = useState({
    title: "",
    category: "",
    description: "",
    price: "",
    location: "",
  });
  const [userId, setUserId] = useState(null); // State to hold userId

  // Fetch userId once when component mounts
  useEffect(() => {
    const fetchUserId = async () => {
      try {
        const userData = await getCurrentUser(); // Adjust this function as per your actual implementation
        setUserId(userData.id); // Assuming userData has an id property
      } catch (error) {
        console.error("Error fetching user data:", error);
        // Handle error fetching user data
      }
    };

    fetchUserId();
  }, []);

  const handleImageSelect = (index, file) => {
    const newImages = [...images];
    newImages[index] = { file: file, name: file.name };
    setImages(newImages);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Check if all fields are complete
    const allFieldsComplete = Object.values(formData).every(
      (value) => value !== ""
    );

    if (!allFieldsComplete) {
      alert("Please complete all fields.");
      return;
    }

    try {
      // Include userId in formData if available
      const formDataWithUserId = {
        ...formData,
        userId: userId,
      };

      console.log("Form Data:", JSON.stringify(formDataWithUserId));

      const formDataObject = new FormData();
      formDataObject.append("formData", JSON.stringify(formDataWithUserId));

      // Append images to formDataObject
      images.forEach((image, index) => {
        if (image.file) {
          formDataObject.append(`image${index}`, image.file, image.name);
        }
      });

      const itemResponse = await fetch(
        `http://${config.host}:3001/api/insertItem`,
        {
          method: "POST",
          body: formDataObject,
        }
      );

      if (!itemResponse.ok) {
        throw new Error("Failed to submit item data");
      }

      // Handle image upload separately if needed

      alert("Item and images submitted successfully");
      // Reset form and images
      setFormData({
        title: "",
        category: "",
        description: "",
        price: "",
        location: "",
      });
      setImages(Array(8).fill({ file: null, name: "" }));
    } catch (error) {
      console.error("Error:", error);
      alert("An error occurred while submitting the item");
    }
  };

  // Render form once userId is fetched
  if (userId === null) {
    return null; // Render loading indicator or handle case where userId is not available
  }

  return (
    <React.StrictMode>
      <Header />
      <form onSubmit={handleSubmit}>
        <label>
          Title:
          <input
            type="text"
            name="title"
            value={formData.title}
            onChange={handleInputChange}
            required
          />
        </label>
        <br />
        <label>
          Category:
          <select
            name="category"
            value={formData.category}
            onChange={handleInputChange}
            required
          >
            <option value="" disabled>
              Select a category
            </option>
            <option value="cat1">cat1</option>
            <option value="cat2">cat2</option>
            <option value="cat3">cat3</option>
          </select>
        </label>
        <br />
        <label>
          Description:
          <textarea
            name="description"
            value={formData.description}
            onChange={handleInputChange}
            required
          />
        </label>
        <br />
        <label>
          Price:
          <input
            type="number"
            name="price"
            value={formData.price}
            onChange={handleInputChange}
            required
          />
        </label>
        <br />
        <label>
          Location (using map):
          <input
            type="text"
            name="location"
            value={formData.location}
            onChange={handleInputChange}
            required
          />
          {/* Replace this input with your map component */}
        </label>
        <br />
        <label>Add images of the item</label>
        <div className="images-container">
          {images.map((_, index) => (
            <DragAndDropImageInput
              key={index}
              onImageSelect={(file) => handleImageSelect(index, file)}
            />
          ))}
        </div>
        <button type="submit">Submit</button>
      </form>
    </React.StrictMode>
  );
};

export default AddItem;
