import React, { useState, useEffect } from "react";
import FavCard from "./components/FavCard.jsx";
import { getCurrentUser } from "./app.js";
import Header from "./components/Header.jsx";
const config = require("./configs/config.js");

const Favourites = () => {
  const [favs, setFavs] = useState([]);
  const [userID, setUserID] = useState(null);

  useEffect(() => {
    const fetchFavs = async () => {
      try {
        const user = await getCurrentUser();
        console.log(user.id);
        setUserID(user.id);

        const response = await fetch(
          `http://${config.host}:3001/api/getAllFavorites`,
          {
            method: "POST",
            headers: {
              "Content-Type": "application/json",
            },
            body: JSON.stringify({ id: userID }), // Use user.id here directly
          }
        );

        if (!response.ok) {
          throw new Error(
            `Error fetching favourite items: ${response.statusText}`
          );
        }

        const data = await response.json();
        setFavs(data); // Set the items in state
      } catch (error) {
        console.error("Error fetching favourite items: ", error);
      }
    };

    fetchFavs(); // Call fetchFavs inside useEffect to avoid infinite loop
  }, []); // Empty dependency array means this effect runs only once, similar to componentDidMount

  console.log("favs:", favs); // Log favs to check its structure

  
  return (
    <React.StrictMode>
      <Header />
      {favs.map((fav) => (
        <FavCard
          key={fav.id} // Ensure fav.id is unique for each FavCard
          id={fav.id}
          name={fav.title}
          img={fav.img}
          price={fav.price}
        />
      ))}
    </React.StrictMode>
  );
};

export default Favourites;
