import React, { useState, useEffect } from "react";
import FavCard from "./components/FavCard.jsx";
const config = require("./configs/config.js");

const Favourites = () => {
  const [favs, setFavs] = useState([]);

  useEffect(() => {
    fetchFavs();
  });

  const fetchFavs = async () => {
    try {
      const response = await fetch(
        `http://${config.host}:3001/api/getAllFavorites`,
        {
          method: "POST",
          headers: {
            "Content-Type": "json",
          },
          body: JSON.stringify({ id }),
        }
      );

      if (!response.ok) {
        throw new Error(
          `Error fetching favourite items: ${response.statusText}`
        );
      }

      const data = await response.json();
      console.log("Favourites: ", data);
      setFavs(data); // Set the items in state
    } catch (error) {
      console.error("Error fetching favourite items: ", error);
    }

    return (
      <React.StrictMode>
        {favs.map((fav) => (
          <FavCard
            key={fav.id} // Make sure to provide a unique key for each item
            id={fav.id}
            name={fav.title}
            img={fav.img}
            price={fav.price}
          />
        ))}
      </React.StrictMode>
    );
  };
};

export default Favourites;
