import React, { useState } from "react";
import DragAndDropImageInput from "./components/DragAndDropImageInput"; // Adjust the import based on your file structure
import "./components/DragAndDropImageInput.css"; // Make sure to import the CSS

const config = require("./configs/config.js");

const AddItem = () => {
  const [images, setImages] = useState(Array(8).fill(null));
  const [formData, setFormData] = useState({
    title: '',
    category: '',
    description: '',
    price: '',
    location: '',
  });

  const handleImageSelect = (index, file) => {
    const newImages = [...images];
    newImages[index] = file;
    setImages(newImages);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Check if all fields are complete
    const allFieldsComplete = Object.values(formData).every(value => value !== '') && images.every(image => image !== null);

    if (!allFieldsComplete) {
      alert('Please complete all fields and upload all images.');
      return;
    }

    try {
      // Send form data and image names to /api/insertItem
      const itemResponse = await fetch(`${config.apiUrl}/api/insertItem`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          ...formData,
          imageNames: images.map(image => image.name),
        }),
      });

      if (!itemResponse.ok) {
        throw new Error('Failed to submit item data');
      }

      // Send each image to /api/insertImage
      for (const image of images) {
        const formData = new FormData();
        formData.append('image', image);
        formData.append('folder', 'item-'); // Adjust folder name as needed
        formData.append('fileName', image.name);

        const imageResponse = await fetch(`${config.apiUrl}/api/insertImage`, {
          method: 'POST',
          body: formData,
        });

        if (!imageResponse.ok) {
          throw new Error('Failed to upload image');
        }
      }

      alert('Item and images submitted successfully');
      // Reset form and images
      setFormData({
        title: '',
        category: '',
        description: '',
        price: '',
        location: '',
      });
      setImages(Array(8).fill(null));
    } catch (error) {
      console.error('Error:', error);
      alert('An error occurred while submitting the item');
    }
  };

  return (
    <React.StrictMode>
      <form onSubmit={handleSubmit}>
        <label>
          Title:
          <input
            type="text"
            name="title"
            value={formData.title}
            onChange={handleInputChange}
            required
          />
        </label>
        <br />
        <label>
          Category:
          <select name="category" value={formData.category} onChange={handleInputChange} required>
            <option value="" disabled>Select a category</option>
            <option value="cat1">cat1</option>
            <option value="cat2">cat2</option>
            <option value="cat3">cat3</option>
          </select>
        </label>
        <br />
        <label>
          Description:
          <textarea
            name="description"
            value={formData.description}
            onChange={handleInputChange}
            required
          />
        </label>
        <br />
        <label>
          Price:
          <input
            type="number"
            name="price"
            value={formData.price}
            onChange={handleInputChange}
            required
          />
        </label>
        <br />
        <label>
          Location (using map):
          <input
            type="text"
            name="location"
            value={formData.location}
            onChange={handleInputChange}
            required
          />
          {/* Replace this input with your map component */}
        </label>
        <br />
        <label>Add images of the item</label>
        <div className="images-container">
          {images.map((_, index) => (
            <DragAndDropImageInput
              key={index}
              onImageSelect={(file) => handleImageSelect(index, file)}
            />
          ))}
        </div>
        <button type="submit">Submit</button>
      </form>
    </React.StrictMode>
  );
};

export default AddItem;
