import React from "react";
import { Link } from "react-router-dom";
const config = require("../configs/config.js");

const FavCard = (props) => {
  return (
    <div className="item">
      <Link to={`item/${props.id}`}>
        <img
          src={`http://${config.host}:3001/images/${props.img}`}
          alt={`img: ${props.name}`}
        />
        <p>{props.name}</p>
        <p>{props.price} $</p>
      </Link>
    </div>
  );
};

export default FavCard;
